import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function SignupPage() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    password: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Sign up with:", formData);
  };

  return (
   <div className="min-h-screen bg-[#2c2c2c] flex items-center justify-center relative px-4 text-white">
      {/* Background Logo (not dimmed) */}
      <img
  src="/assets/logo.png"
  alt="Background Logo"
  className="absolute w-[900px] z-0"
  style={{
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    pointerEvents: "none",
    filter: "brightness(0)", // makes image fully black
  }}
/>


      {/* Form */}
      <form
  onSubmit={handleSubmit}
  className="z-10 w-full max-w-sm"
>
  <div className="flex flex-col space-y-4">
    <input
  type="text"
  name="firstName"
  placeholder="First Name"
  value={formData.firstName}
  onChange={handleChange}
  className="w-70 bg-gray-400 text-white font-bold px-4 py-3 rounded-md placeholder-white focus:outline-none"
/>
    <input
      type="text"
      name="lastName"
      placeholder="Last Name"
      value={formData.lastName}
      onChange={handleChange}
      className="bg-gray-400 text-white font-bold px-4 py-3 rounded-md placeholder-white focus:outline-none"
    />
    <input
      type="password"
      name="password"
      placeholder="Password"
      value={formData.password}
      onChange={handleChange}
      className="bg-gray-400 text-white font-bold px-4 py-3 rounded-md placeholder-white focus:outline-none"
    />
  </div>

  {/* ⬇️ This is separated to push it downward */}
  <div className="mt-12 flex justify-center">
    <button
      type="submit"
      className="bg-gray-400 text-white font-bold py-2 px-6 rounded-md hover:bg-gray-500 transition"
    >
      Create your account
    </button>
  </div>
</form>

    </div>
  );
}
